/*!40101 SET NAMES utf8 */;

/*!40101 SET SQL_MODE=''*/;

/*!40014 SET @OLD_UNIQUE_CHECKS=@@UNIQUE_CHECKS, UNIQUE_CHECKS=0 */;
/*!40014 SET @OLD_FOREIGN_KEY_CHECKS=@@FOREIGN_KEY_CHECKS, FOREIGN_KEY_CHECKS=0 */;
/*!40101 SET @OLD_SQL_MODE=@@SQL_MODE, SQL_MODE='NO_AUTO_VALUE_ON_ZERO' */;
/*!40111 SET @OLD_SQL_NOTES=@@SQL_NOTES, SQL_NOTES=0 */;
CREATE DATABASE /*!32312 IF NOT EXISTS*/`blog` /*!40100 DEFAULT CHARACTER SET utf8 */;

USE `blog`;

/*Table structure for table `zt_blog` */

DROP TABLE IF EXISTS `zt_blog`;

CREATE TABLE `zt_blog` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(50) NOT NULL DEFAULT '' COMMENT '标题',
  `content` text NOT NULL COMMENT '内容',
  `pic` varchar(50) NOT NULL DEFAULT '' COMMENT '图片',
  `author` varchar(10) NOT NULL DEFAULT '' COMMENT '作者',
  `read_num` int(11) NOT NULL DEFAULT '0' COMMENT '访问次数',
  `description` varchar(500) NOT NULL DEFAULT '' COMMENT '描述',
  `parent_categroy_id` int(11) NOT NULL DEFAULT '0' COMMENT '父类ID',
  `categroy_id` int(11) NOT NULL DEFAULT '0' COMMENT '分类id',
  `create_time` int(11) NOT NULL DEFAULT '0' COMMENT '创建时间',
  `uodate_time` int(11) NOT NULL DEFAULT '0' COMMENT '修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

/*Data for the table `zt_blog` */

insert  into `zt_blog`(`id`,`title`,`content`,`pic`,`author`,`read_num`,`description`,`parent_categroy_id`,`categroy_id`,`create_time`,`uodate_time`) values (1,'IP要突破2000+了','&lt;p&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: 宋体, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 28px; text-indent: 28px; white-space: normal;&quot;&gt;很久都没有更新网站了，现在得空看了下网站怎么样了，没有想到，百度权重4,PR3,访问IP也在上升，一年的时间没有更新，看来一次性做好一个网站很有必要，这回一劳永逸了 。。。。不过，我还是得更新模板，要不然损失好些老朋友呢&lt;/span&gt;&lt;img src=&quot;http://yangqing.com/Public/vendor/umeditor/php/upload/20160603/14649446131411.jpg&quot; _src=&quot;http://yangqing.com/Public/vendor/umeditor/php/upload/20160603/14649446131411.jpg&quot;/&gt;&lt;/p&gt;','upload/2016-06-03/5751482fae8eb.jpg','杨青',1,'  很久都没有更新网站了，现在得空看了下网站怎么样了，没有想到，百度权重4,PR3,访问IP也在上升，一年的时间没有更新，看来一次性做好一个网站很有必要，这回一劳永逸了 。。hahaha。。不过，我还是得更新模板，要不然损失好些老朋友呢\r\n              ',1,2,1464944632,0),(2,'【孕期日记】生活本该如此','&lt;ul class=&quot;infos&quot; style=&quot;margin: 20px 30px 20px 0px; padding: 0px; list-style: none; overflow: hidden; line-height: 28px; font-size: 14px; text-indent: 2em; color: rgb(102, 102, 102); font-family: 宋体, Arial, Helvetica, sans-serif; white-space: normal;&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;这是第一篇2015年的生活日记，我的这个博客也因为孕期给耽搁了不少，要不然每个月都能出一两套模板，或者定时更新一些技术文章，群里面也很少参与讨论。现在因为这个即将到来的小家伙，我不得不舍弃一些时间和精力。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;以前说过，女程序员的职业规划，大多数在婚姻后都选择了家庭。我也不例外，家为主，事业为辅。打理博客和做网站成了业余的工作。现在逛母婴论坛，参与活动，比我自己的博客还勤快。昨天也因为攒积分兑换玩具和尿布的事情，发动了一些我能肯定他们能帮忙的朋友，来帮我注册攒积分。当天更新积分后我就兑换了1700个积分。有不少朋友还召集了身边其他人来帮忙，真是感动无比。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;以前每个月拿固定工资任性挥霍的日子不在了，换来了生活的柴米油盐，迎接一个新的生命，又会过渡到另外一种生活状态，不再计较是否是名牌，兴许对自己越来越抠抠搜搜，越来越喜欢打折、免费的商品...&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;其实生活本该如此！&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;PS：如果你也愿意帮我攒积分，请拿出你的手机，加微信公众号“帮宝适”选择第一个官方认证的帮宝适，然后点击“成长乐园”中的“新会员注册”，关键的一步就是填写我的注册邀请码 8X40C750 接下来的步骤就随你自己了，地址信息可以不用真实的，但你周围如果有其他朋友需要，可以送他们哦~！&lt;/p&gt;&lt;/ul&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','upload/2016-06-03/57514d72c15e3.jpg','刘春阳',4,'这是第一篇2015年的生活日记，我的这个博客也因为孕期给耽搁了不少，要不然每个月都能出一两套模板，或者定时更新一些技术文章，群里面也很少参与讨论。现在因为这个即将到来的小家伙，我不得不舍弃一些时间和精力。',1,3,1464946034,0),(3,'测试用','&lt;p&gt;&lt;img src=&quot;http://yangqing.com/Public/vendor/umeditor/php/upload/20160606/14652121956196.jpg&quot; _src=&quot;http://yangqing.com/Public/vendor/umeditor/php/upload/20160606/14652121956196.jpg&quot;/&gt;这里我可以写一些输入提示&lt;/p&gt;','upload/2016-06-06/57555d3f96c44.jpg','哦哈哈',3,' \r\n             阿达十多年',7,8,1465212223,0),(4,'这是enme de ','&lt;p&gt;&lt;img src=&quot;http://yangqing.com/Public/vendor/umeditor/php/upload/20160606/14652122434822.jpg&quot; _src=&quot;http://yangqing.com/Public/vendor/umeditor/php/upload/20160606/14652122434822.jpg&quot;/&gt;这里我可以写一些输入提示&lt;/p&gt;','upload/2016-06-06/57555d6b525e7.jpg','老兵',6,' 纹身是一种艺术\r\n              ',1,4,1465212267,0),(5,'【匆匆那些年】总结个人博客经历的这四年…','&lt;ul class=&quot;infos&quot; style=&quot;margin: 20px 30px 20px 0px; padding: 0px; list-style: none; overflow: hidden; line-height: 28px; font-size: 14px; text-indent: 2em; color: rgb(102, 102, 102); font-family: 宋体, Arial, Helvetica, sans-serif; white-space: normal;&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;博客从最初的域名购买，到上线已经有四年的时间了，这四年的时间，有笑过，有怨过，有悔过，有执着过，也有放弃过…但最后还是坚持了下来，时间如此匆匆，等再回过头已来不及去弥补…&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;其实蛮遗憾的，话说这四年，并不是很专注于它，起初的建立是因为“面子”，一个网页设计师怎能没有一个属于自己的博客网站，域名几番斟酌选了yangqq(唯一用心的选了域名)，选好后，草草的用了两天时间，设计了一个简单的博客模板，接下来用了漏洞百出的asp程序(自己瞎改)，从网上copy了很多文章，放到网站上，就这样，我满心欢喜…博客从此也就搁置了。就像玩拼图，趁着三分热度和新奇，一鼓作气完成了，就随意的丢在犄角旮旯了，再也没有兴趣…&lt;/p&gt;&lt;/ul&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','upload/2016-06-06/57555f83d3a05.jpg','牛',70,' \r\n              博客从最初的域名购买，到上线已经有四年的时间了，这四年的时间，有笑过，有怨过，有悔过，有执着过，也有放弃过…但最后还是坚持了下来，时间如此匆匆，等再回过头已来不及去弥补…',7,8,1465212803,0),(6,'帝国cms常用标签调用方法总结（不得不收藏哦）','&lt;ul class=&quot;infos&quot; style=&quot;margin: 20px 30px 20px 0px; padding: 0px; list-style: none; overflow: hidden; line-height: 28px; font-size: 14px; text-indent: 2em; color: rgb(102, 102, 102); font-family: 宋体, Arial, Helvetica, sans-serif; white-space: normal;&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;整理了一些常用的帝国cms调用，灵动标签和万能标签的调用方法举例。幻灯片、标题、一级栏目、二级栏目、带模版的友情链接（下拉菜单）、判断内容页字段为空时是如何调用的等等...新手可以借鉴学习，这也是我常用来copy直接用的，节省了不少时间。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;幻灯片：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;1、&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;显示表最新的头条信息&lt;/strong&gt;（数字13代表头条，数字3代表最新）&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[phomeflashpic]0,4,280,255,0,0,13,3[/phomeflashpic]&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;2、&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;显示栏目最新的推荐信息并且显示标题&lt;/strong&gt;（数字2代表栏目推荐，最后的数字0代表栏目最新）&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[phomeflashpic]7,4,280,255,1,40,2,0[/phomeflashpic]&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;标题+简介&lt;/strong&gt;（灵动标签的调用方法）&lt;/p&gt;&lt;ul class=&quot;infos&quot; style=&quot;margin: 20px 30px 20px 0px; padding: 0px; list-style: none; overflow: hidden; line-height: 28px; font-size: 14px; text-indent: 2em; color: rgb(102, 102, 102); font-family: 宋体, Arial, Helvetica, sans-serif; white-space: normal;&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/bj/2014-10-18/4a79882abb9f9caf0d6ede90ec597f54.jpg&quot; alt=&quot;帝国cms常用标签调用方法总结&quot; width=&quot;461&quot; height=&quot;81&quot; style=&quot;margin: auto; padding: 0px; display: block;&quot;/&gt;&amp;nbsp;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[e:loop={2,1,0,0}]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp;&amp;lt;h1&amp;gt;&amp;lt;a href=&amp;quot;&amp;lt;?=$bqsr[titleurl]?&amp;gt;&amp;quot; target=&amp;quot;_blank&amp;quot; title=&amp;quot;&amp;lt;?=$bqr[title]?&amp;gt;&amp;quot;&amp;gt; &amp;lt;?=esub($bqr[title],36)?&amp;gt; &amp;lt;/a&amp;gt;&amp;lt;/h1&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;p&amp;gt;&amp;lt;?=esub($bqr[smalltext],88)?&amp;gt; &amp;lt;/p&amp;gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[/e:loop]&amp;nbsp;&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;时间（格式m-d）+标题&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;1、灵动标签调用方法：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;举例：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/bj/2014-10-18/fe6ef89035914014f6563eea77ce2c07.jpg&quot; alt=&quot;帝国cms常用标签调用方法总结&quot; width=&quot;377&quot; height=&quot;114&quot; style=&quot;margin: auto; padding: 0px; display: block;&quot;/&gt;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[e:loop={2,7,0,0}]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;li&amp;gt;&amp;lt;span&amp;gt;[&amp;lt;?=date(&amp;#39;m-d&amp;#39;,$bqr[newstime])?&amp;gt;]&amp;lt;/span&amp;gt;&amp;lt;a href=&amp;quot;&amp;lt;?=$bqsr[titleurl]?&amp;gt;&amp;quot; target=&amp;quot;_blank&amp;quot; title=&amp;quot;&amp;lt;?=$bqr[title]?&amp;gt;&amp;quot;&amp;gt;&amp;lt;?=esub($bqr[title],42)?&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[/e:loop]&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;2、万能标签调用方法：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;页面内容模板：[!--empirenews.listtemp--]&amp;lt;!--list.var1--&amp;gt;[!--empirenews.listtemp--]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;列表内容模板：&amp;lt;li&amp;gt;&amp;lt;span&amp;gt;[!--newstime--]&amp;lt;/span&amp;gt;&amp;lt;a href=&amp;quot;[!--titleurl--]&amp;quot; title=&amp;quot;[!--oldtitle--]&amp;quot;&amp;gt;[!--title--]&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;首页调用（举例）：[ecmsinfo]1,7,20,0,0,12,0[/ecmsinfo] &amp;nbsp; 数字12是模板标签id&amp;nbsp;&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;标题+new图片（最后更新时间）&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/bj/2014-10-18/867c58c00be0dfd3f0515bc7909479aa.jpg&quot; alt=&quot;帝国cms常用标签调用方法总结&quot; width=&quot;360&quot; height=&quot;95&quot; style=&quot;margin: auto; padding: 0px; display: block;&quot;/&gt;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[e:loop={1,5,0,0}]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;?&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;$newimg=&amp;quot;&amp;quot;;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;if(date(&amp;quot;m-d&amp;quot;,$bqr[truetime])==date(&amp;quot;m-d&amp;quot;))&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;{&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;$newimg=&amp;quot;&amp;lt;img src=&amp;#39;[!--news.url--]skin/gh/images/new.gif&amp;#39;&amp;gt;&amp;quot;;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;}&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;?&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;li&amp;gt;&amp;lt;a href=&amp;quot;&amp;lt;?=$bqsr[titleurl]?&amp;gt;&amp;quot; target=&amp;quot;_blank&amp;quot; title=&amp;quot;&amp;lt;?=$bqr[title]?&amp;gt;&amp;quot;&amp;gt;&amp;lt;?=esub($bqr[title],44)?&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;?=$newimg?&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[/e:loop]&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;图片列表&lt;/strong&gt;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[e:loop={&amp;#39;news&amp;#39;,5,18,1}]&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;li&amp;gt;&amp;lt;a href=&amp;quot;&amp;lt;?=$bqsr[titleurl]?&amp;gt;&amp;quot; title=&amp;quot;&amp;lt;?=$bqr[title]?&amp;gt;&amp;quot; target=&amp;quot;_blank&amp;quot;&amp;gt;&amp;lt;img src=&amp;quot;&amp;lt;?=$bqr[titlepic]?&amp;gt;&amp;quot; &amp;nbsp;/&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[/e:loop]&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;栏目导航列表&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;1、灵动标签调用方法：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[e:loop={&amp;quot;select classid,classname from {$dbtbpre}enewsclass where bclassid=0 order by myorder,classid desc&amp;quot;,0,24,0}]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;?php&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;$classurl=sys_ReturnBqClassname($bqr,9);//取得栏目地址&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;?&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;li&amp;gt;&amp;lt;a href=&amp;quot;&amp;lt;?=$classurl?&amp;gt;&amp;quot;&amp;gt;&amp;lt;?=$bqr[classname]?&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[/e:loop]&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;2、万能标签调用方法：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;首页一级导航：[showclasstemp]&amp;#39;0&amp;#39;,12,0,0[/showclasstemp] 数字12是标签id&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;当前列表页（有二级栏目显示二级，没有显示一级）：[showclasstemp]&amp;#39;selfinfo&amp;#39;,1,0,0[/showclasstemp] 数字1是标签id&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;二级导航下拉菜单：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/bj/2014-10-18/7cfa27060778017fc68ca0d2b2e9eaea.jpg&quot; alt=&quot;帝国cms常用标签调用方法总结&quot; width=&quot;87&quot; height=&quot;149&quot; style=&quot;margin: auto; padding: 0px; display: block;&quot;/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;页面模板内容：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;li&amp;gt;&amp;lt;a href=&amp;quot;[!--bclassurl--]&amp;quot;&amp;gt;[!--bclassname--]&amp;lt;/a&amp;gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;ul&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[!--empirenews.listtemp--]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;!--list.var1--&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[!--empirenews.listtemp--]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;/ul&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;列表内容模板：&amp;lt;li&amp;gt;&amp;lt;a href=&amp;quot;[!--classurl--]&amp;quot;&amp;gt;[!--classname--]&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;对应页面调用：[listshowclass]&amp;#39;0&amp;#39;,12,0,0[/listshowclass] 数字12是标签id&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;横向简易搜索&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;举例：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/bj/2014-10-18/c78be876472cc61a6c8b4a3d8da980fd.jpg&quot; alt=&quot;帝国cms常用标签调用方法总结&quot; width=&quot;270&quot; height=&quot;34&quot; style=&quot;margin: auto; padding: 0px; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;css：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;.input_text { background: #FFF url(../images/sub_text.gif) no-repeat left; padding-left: 30px; color: #DFDFDF; height: 22px; border: 0; float: left; border: #d6d6d6 1px solid }&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;.input_submit { background: url(../images/search.jpg) no-repeat; width: 50px; border: none; cursor: pointer; height: 22px; margin-left: 10px }&lt;/p&gt;&lt;/div&gt;&lt;div style=&quot;margin: 0px; padding: 0px;&quot;&gt;涉及图片：&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/bj/2014-10-18/6f0ddc1822646daa7c6a9602859ebba2.gif&quot; alt=&quot;帝国cms常用标签调用方法总结&quot; width=&quot;24&quot; height=&quot;16&quot; style=&quot;margin: 0px; padding: 0px; display: block;&quot;/&gt;&amp;nbsp;&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/bj/2014-10-18/fc16fb16e55053771cfb638e6dc0b152.jpg&quot; alt=&quot;帝国cms常用标签调用方法总结&quot; width=&quot;50&quot; height=&quot;22&quot; style=&quot;margin: 0px; padding: 0px; display: block;&quot;/&gt;&lt;/div&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;form action=&amp;quot;[!--news.url--]e/search/index.php&amp;quot; method=&amp;quot;post&amp;quot; name=&amp;quot;searchform&amp;quot; id=&amp;quot;searchform&amp;quot;&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;lt;input name=&amp;quot;keyboard&amp;quot; type=&amp;quot;text&amp;quot; class=&amp;quot;input_text&amp;quot; &amp;nbsp;id=&amp;quot;keyboard&amp;quot; value=&amp;quot;请输入关键词&amp;quot; onfocus=&amp;quot;if(this.value==&amp;#39;请输入关键词&amp;#39;){this.value=&amp;#39;&amp;#39;};this.style.color=&amp;#39;black&amp;#39;;&amp;quot; onblur=&amp;quot;if(this.value==&amp;#39;&amp;#39;||this.value==&amp;#39;请输入关键词&amp;#39;){this.value=&amp;#39;请输入关键词&amp;#39;;this.style.color=&amp;#39;#DFDFDF&amp;#39;;}&amp;quot;/&amp;gt;&amp;lt;input type=&amp;quot;hidden&amp;quot; name=&amp;quot;show&amp;quot; value=&amp;quot;title&amp;quot; /&amp;gt;&amp;lt;input type=&amp;quot;hidden&amp;quot; name=&amp;quot;tempid&amp;quot; value=&amp;quot;1&amp;quot; /&amp;gt;&amp;lt;input name=&amp;quot;tbname&amp;quot; type=&amp;quot;hidden&amp;quot; value=&amp;quot;news&amp;quot;&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;lt;input type=&amp;quot;submit&amp;quot; name=&amp;quot;Submit&amp;quot; value=&amp;quot;&amp;quot; class=&amp;quot;input_submit&amp;quot; /&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;/form&amp;gt;&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;栏目图片调用（标签模板）&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;1、万能标签调用方法：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;页面模板内容：[!--empirenews.listtemp--]&amp;lt;!--list.var1--&amp;gt;[!--empirenews.listtemp--]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;列表内容模板：&amp;lt;li&amp;gt;&amp;lt;a href=&amp;quot;[!--classurl--]&amp;quot; title=&amp;quot;[!--classname--]&amp;quot;&amp;gt;&amp;lt;img src=&amp;quot;[!--classimg--]&amp;quot; /&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;首页调用：[showclasstemp]67,21,0,0[/showclasstemp] 67是id 21是模板&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;2、灵动标签调用方法（前12条记录）：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[e:loop={&amp;quot;select classid,classname,classimg from {$dbtbpre}enewsclass where bclassid=0 order by myorder,classid desc limit 12&amp;quot;,0,24,0}]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;?php&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;$classurl=sys_ReturnBqClassname($bqr,9);//取得栏目地址&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;?&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;li&amp;gt;&amp;lt;a href=&amp;quot;&amp;lt;?=$classurl?&amp;gt;&amp;quot; target=&amp;quot;_blank&amp;quot;&amp;gt;&amp;lt;img src=&amp;quot;&amp;lt;?=$bqr[classimg]?&amp;gt;&amp;quot;&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;?=$bqr[classname]?&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[/e:loop]&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;友情链接&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;1、带模版的下拉友情链接列表&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/bj/2014-10-18/f96e3df922fd22fc4a106a5cf57b8bb2.jpg&quot; alt=&quot;帝国cms常用标签调用方法总结&quot; width=&quot;222&quot; height=&quot;84&quot; style=&quot;margin: auto; padding: 0px; display: block;&quot;/&gt;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;页面模板内容：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;select onchange=&amp;quot;window.open(this.options[selectedIndex].value,&amp;#39;&amp;#39;,&amp;#39;&amp;#39;);&amp;quot; class=&amp;quot;selink&amp;quot; &amp;nbsp;name=&amp;#39;friendsite&amp;#39;&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp;[!--empirenews.listtemp--]&amp;lt;!--list.var1--&amp;gt;[!--empirenews.listtemp--]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;/select&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;列表内容模板：&amp;lt;option value=&amp;#39;[!--titleurl--]&amp;#39;&amp;gt;[!--title--]&amp;lt;/option&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;对应页面调用：[ecmsinfo]&amp;#39;select lname as title,lurl as titleurl,lid as id from [!db.pre!]enewslink where classid=1 order by lid limit 50&amp;#39;,0,50,0,24,14,0[/ecmsinfo]（14是模板标签 第一个50是数量）&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;2、友情链接图片：&lt;/strong&gt;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[e:loop={&amp;#39;select lname,lurl,lpic from [!db.pre!]enewslink where checked=1 and classid=5 order by myorder,lid&amp;#39;,3,24,0}]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;li&amp;gt;&amp;lt;a href=&amp;quot;&amp;lt;?=$bqr[lurl]?&amp;gt;&amp;quot; target=&amp;quot;_blank&amp;quot; &amp;gt;&amp;lt;img src=&amp;quot;&amp;lt;?=$bqr[lpic]?&amp;gt;&amp;quot;&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp;[/e:loop]&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;留言板时间格式修改&lt;/strong&gt;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;?=format_datetime($r[lytime],&amp;#39;Y-m-d H:i&amp;#39;)?&amp;gt;&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;?=format_datetime($r[lytime],&amp;#39;Y-m-d&amp;#39;)?&amp;gt;&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;用if判断内容模板字段为空时&lt;/strong&gt;：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;1、举例：如果来源为空，则显示“杨青博客网站”&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;?&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;if($navinfor[&amp;#39;befrom&amp;#39;]==&amp;quot;&amp;quot;) {&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;echo &amp;#39;杨青博客网站&amp;#39;;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;}&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;else{&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;echo &amp;#39;&amp;#39;.$navinfor[befrom].&amp;#39;&amp;#39;;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;}&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;?&amp;gt;&amp;nbsp;&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;2、如果作者为空，则不输出任何内容，那么添加样式以及作者&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;?&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;if($navinfor[&amp;#39;writer&amp;#39;]==&amp;quot;&amp;quot;) {&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;echo &amp;#39;&amp;#39;;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;}&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;else{&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;echo &amp;#39;&amp;lt;span&amp;gt;作者：&amp;#39;.$navinfor[writer].&amp;#39;&amp;lt;/span&amp;gt;&amp;#39;;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;}&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;?&amp;gt;&amp;nbsp;&lt;/p&gt;&lt;/div&gt;&lt;/ul&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;/ul&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','upload/2016-06-06/57555ff301c0a.jpg','呀哈哈',54,' \r\n              整理了一些常用的帝国cms调用，灵动标签和万能标签的调用方法举例。幻灯片、标题、一级栏目、二级栏目、带模版的友情链接（下拉菜单）、判断内容页字段为空时是如何调用的等等...新手可以借鉴学习，这也是我常用来copy直接用的，节省了不少时间。',7,8,1465212915,0);

