/*!40101 SET NAMES utf8 */;

/*!40101 SET SQL_MODE=''*/;

/*!40014 SET @OLD_UNIQUE_CHECKS=@@UNIQUE_CHECKS, UNIQUE_CHECKS=0 */;
/*!40014 SET @OLD_FOREIGN_KEY_CHECKS=@@FOREIGN_KEY_CHECKS, FOREIGN_KEY_CHECKS=0 */;
/*!40101 SET @OLD_SQL_MODE=@@SQL_MODE, SQL_MODE='NO_AUTO_VALUE_ON_ZERO' */;
/*!40111 SET @OLD_SQL_NOTES=@@SQL_NOTES, SQL_NOTES=0 */;
CREATE DATABASE /*!32312 IF NOT EXISTS*/`blog` /*!40100 DEFAULT CHARACTER SET utf8 */;

USE `blog`;

/*Table structure for table `zt_blog` */

DROP TABLE IF EXISTS `zt_blog`;

CREATE TABLE `zt_blog` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(50) NOT NULL DEFAULT '' COMMENT '标题',
  `content` text NOT NULL COMMENT '内容',
  `pic` varchar(50) NOT NULL DEFAULT '' COMMENT '图片',
  `author` varchar(10) NOT NULL DEFAULT '' COMMENT '作者',
  `read_num` int(11) NOT NULL DEFAULT '0' COMMENT '访问次数',
  `description` varchar(500) NOT NULL DEFAULT '' COMMENT '描述',
  `parent_categroy_id` int(11) NOT NULL DEFAULT '0' COMMENT '父类ID',
  `categroy_id` int(11) NOT NULL DEFAULT '0' COMMENT '分类id',
  `create_time` int(11) NOT NULL DEFAULT '0' COMMENT '创建时间',
  `uodate_time` int(11) NOT NULL DEFAULT '0' COMMENT '修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

/*Data for the table `zt_blog` */

insert  into `zt_blog`(`id`,`title`,`content`,`pic`,`author`,`read_num`,`description`,`parent_categroy_id`,`categroy_id`,`create_time`,`uodate_time`) values (1,'IP要突破2000+了','&lt;p&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: 宋体, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 28px; text-indent: 28px; white-space: normal;&quot;&gt;很久都没有更新网站了，现在得空看了下网站怎么样了，没有想到，百度权重4,PR3,访问IP也在上升，一年的时间没有更新，看来一次性做好一个网站很有必要，这回一劳永逸了 。。。。不过，我还是得更新模板，要不然损失好些老朋友呢&lt;/span&gt;&lt;img src=&quot;http://yangqing.com/Public/vendor/umeditor/php/upload/20160603/14649446131411.jpg&quot; _src=&quot;http://yangqing.com/Public/vendor/umeditor/php/upload/20160603/14649446131411.jpg&quot;/&gt;&lt;/p&gt;','upload/2016-06-03/5751482fae8eb.jpg','杨青',1,'  很久都没有更新网站了，现在得空看了下网站怎么样了，没有想到，百度权重4,PR3,访问IP也在上升，一年的时间没有更新，看来一次性做好一个网站很有必要，这回一劳永逸了 。。hahaha。。不过，我还是得更新模板，要不然损失好些老朋友呢\r\n              ',1,2,1464944632,0),(2,'【孕期日记】生活本该如此','&lt;ul class=&quot;infos&quot; style=&quot;margin: 20px 30px 20px 0px; padding: 0px; list-style: none; overflow: hidden; line-height: 28px; font-size: 14px; text-indent: 2em; color: rgb(102, 102, 102); font-family: 宋体, Arial, Helvetica, sans-serif; white-space: normal;&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;这是第一篇2015年的生活日记，我的这个博客也因为孕期给耽搁了不少，要不然每个月都能出一两套模板，或者定时更新一些技术文章，群里面也很少参与讨论。现在因为这个即将到来的小家伙，我不得不舍弃一些时间和精力。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;以前说过，女程序员的职业规划，大多数在婚姻后都选择了家庭。我也不例外，家为主，事业为辅。打理博客和做网站成了业余的工作。现在逛母婴论坛，参与活动，比我自己的博客还勤快。昨天也因为攒积分兑换玩具和尿布的事情，发动了一些我能肯定他们能帮忙的朋友，来帮我注册攒积分。当天更新积分后我就兑换了1700个积分。有不少朋友还召集了身边其他人来帮忙，真是感动无比。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;以前每个月拿固定工资任性挥霍的日子不在了，换来了生活的柴米油盐，迎接一个新的生命，又会过渡到另外一种生活状态，不再计较是否是名牌，兴许对自己越来越抠抠搜搜，越来越喜欢打折、免费的商品...&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;其实生活本该如此！&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;PS：如果你也愿意帮我攒积分，请拿出你的手机，加微信公众号“帮宝适”选择第一个官方认证的帮宝适，然后点击“成长乐园”中的“新会员注册”，关键的一步就是填写我的注册邀请码 8X40C750 接下来的步骤就随你自己了，地址信息可以不用真实的，但你周围如果有其他朋友需要，可以送他们哦~！&lt;/p&gt;&lt;/ul&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','upload/2016-06-03/57514d72c15e3.jpg','刘春阳',4,'这是第一篇2015年的生活日记，我的这个博客也因为孕期给耽搁了不少，要不然每个月都能出一两套模板，或者定时更新一些技术文章，群里面也很少参与讨论。现在因为这个即将到来的小家伙，我不得不舍弃一些时间和精力。',1,3,1464946034,0),(3,'测试用','&lt;p&gt;&lt;img src=&quot;http://yangqing.com/Public/vendor/umeditor/php/upload/20160606/14652121956196.jpg&quot; _src=&quot;http://yangqing.com/Public/vendor/umeditor/php/upload/20160606/14652121956196.jpg&quot;/&gt;这里我可以写一些输入提示&lt;/p&gt;','upload/2016-06-06/57555d3f96c44.jpg','哦哈哈',3,' \r\n             阿达十多年',7,8,1465212223,0),(4,'这是enme de ','&lt;p&gt;&lt;img src=&quot;http://yangqing.com/Public/vendor/umeditor/php/upload/20160606/14652122434822.jpg&quot; _src=&quot;http://yangqing.com/Public/vendor/umeditor/php/upload/20160606/14652122434822.jpg&quot;/&gt;这里我可以写一些输入提示&lt;/p&gt;','upload/2016-06-06/57555d6b525e7.jpg','老兵',6,' 纹身是一种艺术\r\n              ',1,4,1465212267,0),(5,'【匆匆那些年】总结个人博客经历的这四年…','&lt;ul class=&quot;infos&quot; style=&quot;margin: 20px 30px 20px 0px; padding: 0px; list-style: none; overflow: hidden; line-height: 28px; font-size: 14px; text-indent: 2em; color: rgb(102, 102, 102); font-family: 宋体, Arial, Helvetica, sans-serif; white-space: normal;&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;博客从最初的域名购买，到上线已经有四年的时间了，这四年的时间，有笑过，有怨过，有悔过，有执着过，也有放弃过…但最后还是坚持了下来，时间如此匆匆，等再回过头已来不及去弥补…&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;其实蛮遗憾的，话说这四年，并不是很专注于它，起初的建立是因为“面子”，一个网页设计师怎能没有一个属于自己的博客网站，域名几番斟酌选了yangqq(唯一用心的选了域名)，选好后，草草的用了两天时间，设计了一个简单的博客模板，接下来用了漏洞百出的asp程序(自己瞎改)，从网上copy了很多文章，放到网站上，就这样，我满心欢喜…博客从此也就搁置了。就像玩拼图，趁着三分热度和新奇，一鼓作气完成了，就随意的丢在犄角旮旯了，再也没有兴趣…&lt;/p&gt;&lt;/ul&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','upload/2016-06-06/57555f83d3a05.jpg','牛',70,' \r\n              博客从最初的域名购买，到上线已经有四年的时间了，这四年的时间，有笑过，有怨过，有悔过，有执着过，也有放弃过…但最后还是坚持了下来，时间如此匆匆，等再回过头已来不及去弥补…',7,8,1465212803,0),(6,'帝国cms常用标签调用方法总结（不得不收藏哦）','&lt;ul class=&quot;infos&quot; style=&quot;margin: 20px 30px 20px 0px; padding: 0px; list-style: none; overflow: hidden; line-height: 28px; font-size: 14px; text-indent: 2em; color: rgb(102, 102, 102); font-family: 宋体, Arial, Helvetica, sans-serif; white-space: normal;&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;整理了一些常用的帝国cms调用，灵动标签和万能标签的调用方法举例。幻灯片、标题、一级栏目、二级栏目、带模版的友情链接（下拉菜单）、判断内容页字段为空时是如何调用的等等...新手可以借鉴学习，这也是我常用来copy直接用的，节省了不少时间。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;幻灯片：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;1、&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;显示表最新的头条信息&lt;/strong&gt;（数字13代表头条，数字3代表最新）&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[phomeflashpic]0,4,280,255,0,0,13,3[/phomeflashpic]&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;2、&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;显示栏目最新的推荐信息并且显示标题&lt;/strong&gt;（数字2代表栏目推荐，最后的数字0代表栏目最新）&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[phomeflashpic]7,4,280,255,1,40,2,0[/phomeflashpic]&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;标题+简介&lt;/strong&gt;（灵动标签的调用方法）&lt;/p&gt;&lt;ul class=&quot;infos&quot; style=&quot;margin: 20px 30px 20px 0px; padding: 0px; list-style: none; overflow: hidden; line-height: 28px; font-size: 14px; text-indent: 2em; color: rgb(102, 102, 102); font-family: 宋体, Arial, Helvetica, sans-serif; white-space: normal;&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/bj/2014-10-18/4a79882abb9f9caf0d6ede90ec597f54.jpg&quot; alt=&quot;帝国cms常用标签调用方法总结&quot; width=&quot;461&quot; height=&quot;81&quot; style=&quot;margin: auto; padding: 0px; display: block;&quot;/&gt;&amp;nbsp;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[e:loop={2,1,0,0}]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp;&amp;lt;h1&amp;gt;&amp;lt;a href=&amp;quot;&amp;lt;?=$bqsr[titleurl]?&amp;gt;&amp;quot; target=&amp;quot;_blank&amp;quot; title=&amp;quot;&amp;lt;?=$bqr[title]?&amp;gt;&amp;quot;&amp;gt; &amp;lt;?=esub($bqr[title],36)?&amp;gt; &amp;lt;/a&amp;gt;&amp;lt;/h1&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;p&amp;gt;&amp;lt;?=esub($bqr[smalltext],88)?&amp;gt; &amp;lt;/p&amp;gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[/e:loop]&amp;nbsp;&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;时间（格式m-d）+标题&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;1、灵动标签调用方法：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;举例：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/bj/2014-10-18/fe6ef89035914014f6563eea77ce2c07.jpg&quot; alt=&quot;帝国cms常用标签调用方法总结&quot; width=&quot;377&quot; height=&quot;114&quot; style=&quot;margin: auto; padding: 0px; display: block;&quot;/&gt;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[e:loop={2,7,0,0}]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;li&amp;gt;&amp;lt;span&amp;gt;[&amp;lt;?=date(&amp;#39;m-d&amp;#39;,$bqr[newstime])?&amp;gt;]&amp;lt;/span&amp;gt;&amp;lt;a href=&amp;quot;&amp;lt;?=$bqsr[titleurl]?&amp;gt;&amp;quot; target=&amp;quot;_blank&amp;quot; title=&amp;quot;&amp;lt;?=$bqr[title]?&amp;gt;&amp;quot;&amp;gt;&amp;lt;?=esub($bqr[title],42)?&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[/e:loop]&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;2、万能标签调用方法：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;页面内容模板：[!--empirenews.listtemp--]&amp;lt;!--list.var1--&amp;gt;[!--empirenews.listtemp--]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;列表内容模板：&amp;lt;li&amp;gt;&amp;lt;span&amp;gt;[!--newstime--]&amp;lt;/span&amp;gt;&amp;lt;a href=&amp;quot;[!--titleurl--]&amp;quot; title=&amp;quot;[!--oldtitle--]&amp;quot;&amp;gt;[!--title--]&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;首页调用（举例）：[ecmsinfo]1,7,20,0,0,12,0[/ecmsinfo] &amp;nbsp; 数字12是模板标签id&amp;nbsp;&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;标题+new图片（最后更新时间）&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/bj/2014-10-18/867c58c00be0dfd3f0515bc7909479aa.jpg&quot; alt=&quot;帝国cms常用标签调用方法总结&quot; width=&quot;360&quot; height=&quot;95&quot; style=&quot;margin: auto; padding: 0px; display: block;&quot;/&gt;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[e:loop={1,5,0,0}]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;?&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;$newimg=&amp;quot;&amp;quot;;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;if(date(&amp;quot;m-d&amp;quot;,$bqr[truetime])==date(&amp;quot;m-d&amp;quot;))&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;{&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;$newimg=&amp;quot;&amp;lt;img src=&amp;#39;[!--news.url--]skin/gh/images/new.gif&amp;#39;&amp;gt;&amp;quot;;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;}&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;?&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;li&amp;gt;&amp;lt;a href=&amp;quot;&amp;lt;?=$bqsr[titleurl]?&amp;gt;&amp;quot; target=&amp;quot;_blank&amp;quot; title=&amp;quot;&amp;lt;?=$bqr[title]?&amp;gt;&amp;quot;&amp;gt;&amp;lt;?=esub($bqr[title],44)?&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;?=$newimg?&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[/e:loop]&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;图片列表&lt;/strong&gt;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[e:loop={&amp;#39;news&amp;#39;,5,18,1}]&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;li&amp;gt;&amp;lt;a href=&amp;quot;&amp;lt;?=$bqsr[titleurl]?&amp;gt;&amp;quot; title=&amp;quot;&amp;lt;?=$bqr[title]?&amp;gt;&amp;quot; target=&amp;quot;_blank&amp;quot;&amp;gt;&amp;lt;img src=&amp;quot;&amp;lt;?=$bqr[titlepic]?&amp;gt;&amp;quot; &amp;nbsp;/&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[/e:loop]&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;栏目导航列表&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;1、灵动标签调用方法：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[e:loop={&amp;quot;select classid,classname from {$dbtbpre}enewsclass where bclassid=0 order by myorder,classid desc&amp;quot;,0,24,0}]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;?php&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;$classurl=sys_ReturnBqClassname($bqr,9);//取得栏目地址&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;?&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;li&amp;gt;&amp;lt;a href=&amp;quot;&amp;lt;?=$classurl?&amp;gt;&amp;quot;&amp;gt;&amp;lt;?=$bqr[classname]?&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[/e:loop]&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;2、万能标签调用方法：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;首页一级导航：[showclasstemp]&amp;#39;0&amp;#39;,12,0,0[/showclasstemp] 数字12是标签id&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;当前列表页（有二级栏目显示二级，没有显示一级）：[showclasstemp]&amp;#39;selfinfo&amp;#39;,1,0,0[/showclasstemp] 数字1是标签id&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;二级导航下拉菜单：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/bj/2014-10-18/7cfa27060778017fc68ca0d2b2e9eaea.jpg&quot; alt=&quot;帝国cms常用标签调用方法总结&quot; width=&quot;87&quot; height=&quot;149&quot; style=&quot;margin: auto; padding: 0px; display: block;&quot;/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;页面模板内容：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;li&amp;gt;&amp;lt;a href=&amp;quot;[!--bclassurl--]&amp;quot;&amp;gt;[!--bclassname--]&amp;lt;/a&amp;gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;ul&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[!--empirenews.listtemp--]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;!--list.var1--&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[!--empirenews.listtemp--]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;/ul&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;列表内容模板：&amp;lt;li&amp;gt;&amp;lt;a href=&amp;quot;[!--classurl--]&amp;quot;&amp;gt;[!--classname--]&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;对应页面调用：[listshowclass]&amp;#39;0&amp;#39;,12,0,0[/listshowclass] 数字12是标签id&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;横向简易搜索&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;举例：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/bj/2014-10-18/c78be876472cc61a6c8b4a3d8da980fd.jpg&quot; alt=&quot;帝国cms常用标签调用方法总结&quot; width=&quot;270&quot; height=&quot;34&quot; style=&quot;margin: auto; padding: 0px; display: block;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;css：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;.input_text { background: #FFF url(../images/sub_text.gif) no-repeat left; padding-left: 30px; color: #DFDFDF; height: 22px; border: 0; float: left; border: #d6d6d6 1px solid }&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;.input_submit { background: url(../images/search.jpg) no-repeat; width: 50px; border: none; cursor: pointer; height: 22px; margin-left: 10px }&lt;/p&gt;&lt;/div&gt;&lt;div style=&quot;margin: 0px; padding: 0px;&quot;&gt;涉及图片：&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/bj/2014-10-18/6f0ddc1822646daa7c6a9602859ebba2.gif&quot; alt=&quot;帝国cms常用标签调用方法总结&quot; width=&quot;24&quot; height=&quot;16&quot; style=&quot;margin: 0px; padding: 0px; display: block;&quot;/&gt;&amp;nbsp;&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/bj/2014-10-18/fc16fb16e55053771cfb638e6dc0b152.jpg&quot; alt=&quot;帝国cms常用标签调用方法总结&quot; width=&quot;50&quot; height=&quot;22&quot; style=&quot;margin: 0px; padding: 0px; display: block;&quot;/&gt;&lt;/div&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;form action=&amp;quot;[!--news.url--]e/search/index.php&amp;quot; method=&amp;quot;post&amp;quot; name=&amp;quot;searchform&amp;quot; id=&amp;quot;searchform&amp;quot;&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;lt;input name=&amp;quot;keyboard&amp;quot; type=&amp;quot;text&amp;quot; class=&amp;quot;input_text&amp;quot; &amp;nbsp;id=&amp;quot;keyboard&amp;quot; value=&amp;quot;请输入关键词&amp;quot; onfocus=&amp;quot;if(this.value==&amp;#39;请输入关键词&amp;#39;){this.value=&amp;#39;&amp;#39;};this.style.color=&amp;#39;black&amp;#39;;&amp;quot; onblur=&amp;quot;if(this.value==&amp;#39;&amp;#39;||this.value==&amp;#39;请输入关键词&amp;#39;){this.value=&amp;#39;请输入关键词&amp;#39;;this.style.color=&amp;#39;#DFDFDF&amp;#39;;}&amp;quot;/&amp;gt;&amp;lt;input type=&amp;quot;hidden&amp;quot; name=&amp;quot;show&amp;quot; value=&amp;quot;title&amp;quot; /&amp;gt;&amp;lt;input type=&amp;quot;hidden&amp;quot; name=&amp;quot;tempid&amp;quot; value=&amp;quot;1&amp;quot; /&amp;gt;&amp;lt;input name=&amp;quot;tbname&amp;quot; type=&amp;quot;hidden&amp;quot; value=&amp;quot;news&amp;quot;&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;lt;input type=&amp;quot;submit&amp;quot; name=&amp;quot;Submit&amp;quot; value=&amp;quot;&amp;quot; class=&amp;quot;input_submit&amp;quot; /&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;/form&amp;gt;&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;栏目图片调用（标签模板）&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;1、万能标签调用方法：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;页面模板内容：[!--empirenews.listtemp--]&amp;lt;!--list.var1--&amp;gt;[!--empirenews.listtemp--]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;列表内容模板：&amp;lt;li&amp;gt;&amp;lt;a href=&amp;quot;[!--classurl--]&amp;quot; title=&amp;quot;[!--classname--]&amp;quot;&amp;gt;&amp;lt;img src=&amp;quot;[!--classimg--]&amp;quot; /&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;首页调用：[showclasstemp]67,21,0,0[/showclasstemp] 67是id 21是模板&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;2、灵动标签调用方法（前12条记录）：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[e:loop={&amp;quot;select classid,classname,classimg from {$dbtbpre}enewsclass where bclassid=0 order by myorder,classid desc limit 12&amp;quot;,0,24,0}]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;?php&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;$classurl=sys_ReturnBqClassname($bqr,9);//取得栏目地址&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;?&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;li&amp;gt;&amp;lt;a href=&amp;quot;&amp;lt;?=$classurl?&amp;gt;&amp;quot; target=&amp;quot;_blank&amp;quot;&amp;gt;&amp;lt;img src=&amp;quot;&amp;lt;?=$bqr[classimg]?&amp;gt;&amp;quot;&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;?=$bqr[classname]?&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[/e:loop]&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;友情链接&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;1、带模版的下拉友情链接列表&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/bj/2014-10-18/f96e3df922fd22fc4a106a5cf57b8bb2.jpg&quot; alt=&quot;帝国cms常用标签调用方法总结&quot; width=&quot;222&quot; height=&quot;84&quot; style=&quot;margin: auto; padding: 0px; display: block;&quot;/&gt;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;页面模板内容：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;select onchange=&amp;quot;window.open(this.options[selectedIndex].value,&amp;#39;&amp;#39;,&amp;#39;&amp;#39;);&amp;quot; class=&amp;quot;selink&amp;quot; &amp;nbsp;name=&amp;#39;friendsite&amp;#39;&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp;[!--empirenews.listtemp--]&amp;lt;!--list.var1--&amp;gt;[!--empirenews.listtemp--]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;/select&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;列表内容模板：&amp;lt;option value=&amp;#39;[!--titleurl--]&amp;#39;&amp;gt;[!--title--]&amp;lt;/option&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;对应页面调用：[ecmsinfo]&amp;#39;select lname as title,lurl as titleurl,lid as id from [!db.pre!]enewslink where classid=1 order by lid limit 50&amp;#39;,0,50,0,24,14,0[/ecmsinfo]（14是模板标签 第一个50是数量）&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;2、友情链接图片：&lt;/strong&gt;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;[e:loop={&amp;#39;select lname,lurl,lpic from [!db.pre!]enewslink where checked=1 and classid=5 order by myorder,lid&amp;#39;,3,24,0}]&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;li&amp;gt;&amp;lt;a href=&amp;quot;&amp;lt;?=$bqr[lurl]?&amp;gt;&amp;quot; target=&amp;quot;_blank&amp;quot; &amp;gt;&amp;lt;img src=&amp;quot;&amp;lt;?=$bqr[lpic]?&amp;gt;&amp;quot;&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp;[/e:loop]&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;留言板时间格式修改&lt;/strong&gt;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;?=format_datetime($r[lytime],&amp;#39;Y-m-d H:i&amp;#39;)?&amp;gt;&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;?=format_datetime($r[lytime],&amp;#39;Y-m-d&amp;#39;)?&amp;gt;&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;用if判断内容模板字段为空时&lt;/strong&gt;：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;1、举例：如果来源为空，则显示“杨青博客网站”&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;?&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;if($navinfor[&amp;#39;befrom&amp;#39;]==&amp;quot;&amp;quot;) {&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;echo &amp;#39;杨青博客网站&amp;#39;;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;}&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;else{&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;echo &amp;#39;&amp;#39;.$navinfor[befrom].&amp;#39;&amp;#39;;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;}&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;?&amp;gt;&amp;nbsp;&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;2、如果作者为空，则不输出任何内容，那么添加样式以及作者&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;?&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;if($navinfor[&amp;#39;writer&amp;#39;]==&amp;quot;&amp;quot;) {&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;echo &amp;#39;&amp;#39;;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;}&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;else{&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;echo &amp;#39;&amp;lt;span&amp;gt;作者：&amp;#39;.$navinfor[writer].&amp;#39;&amp;lt;/span&amp;gt;&amp;#39;;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;}&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;?&amp;gt;&amp;nbsp;&lt;/p&gt;&lt;/div&gt;&lt;/ul&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;/ul&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','upload/2016-06-06/57555ff301c0a.jpg','呀哈哈',54,' \r\n              整理了一些常用的帝国cms调用，灵动标签和万能标签的调用方法举例。幻灯片、标题、一级栏目、二级栏目、带模版的友情链接（下拉菜单）、判断内容页字段为空时是如何调用的等等...新手可以借鉴学习，这也是我常用来copy直接用的，节省了不少时间。',7,8,1465212915,0),(7,'2014年度优秀个人博客评选活动','&lt;ul class=&quot;infos&quot; style=&quot;margin: 20px 30px 20px 0px; padding: 0px; list-style: none; overflow: hidden; line-height: 28px; font-size: 14px; text-indent: 2em; color: rgb(102, 102, 102); font-family: 宋体, Arial, Helvetica, sans-serif; white-space: normal;&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;2014年即将结束，相信很多来此站的朋友都有自己的独立博客，本站欲做个小调查，在此收集个人独立博客网站， 评选出本年度优秀博客网站。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;无论你是使用了本站模板或是其他网站模板还是自己设计，均可参与评选活动！&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;评选活动日期截止到2014年12月31日&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;参赛格式如下:&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;网站名称:&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;网站地址:&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;网站简介:&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;网站程序:&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;个性特点:&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;也可以直接发到我的邮箱，邮箱地址 dancesmiling@qq.com&lt;/p&gt;&lt;/ul&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','upload/2016-06-07/5756108894136.jpg','杨青',0,' \r\n              2014年即将结束，相信很多来此站的朋友都有自己的独立博客，本站欲做个小调查，在此收集个人独立博客网站， 评选出本年度优秀博客网站。\r\n无论你是使用了本站模板或是其他网站模板还是自己设计，均可参与评选活动！',7,11,1465258120,0),(8,'使用CSS3制作文字、图片倒影','&lt;ul class=&quot;infos&quot; style=&quot;margin: 20px 30px 20px 0px; padding: 0px; list-style: none; overflow: hidden; line-height: 28px; font-size: 14px; text-indent: 2em; color: rgb(102, 102, 102); font-family: 宋体, Arial, Helvetica, sans-serif; white-space: normal;&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;CSS3制作文字、图片倒影需要涉及到使用CSS3.0新属性之box-reflect。box-reflect属性目前仅在Chrome、Safari和Opera浏览器下支持，但这并不影响我们来学习这个属性的应用。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;box-reflect语法：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;box-reflect：none | &amp;lt;direction&amp;gt; &amp;lt;offset&amp;gt;? &amp;lt;mask-box-image&amp;gt;?&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;lt;direction&amp;gt; = above | below | left | right&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;lt;offset&amp;gt; = &amp;lt;length&amp;gt; | &amp;lt;percentage&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;lt;mask-box-image&amp;gt; = none | &amp;lt;url&amp;gt; | &amp;lt;linear-gradient&amp;gt; | &amp;lt;radial-gradient&amp;gt; | &amp;lt;repeating-linear-gradient&amp;gt; | &amp;lt;repeating-radial-gradient&amp;gt;&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; 默认值：none&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; 适用于：所有元素&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; 继承性：无&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;由于此属性并不是W3C标准属性，在具体使用之时，还是需要添加浏览器的私有属性，根据浏览器的兼容性，使用box-reflect时需要添加-webkit和前缀：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;-webkit-box-reflect：none | &amp;lt;direction&amp;gt; &amp;lt;offset&amp;gt;? &amp;lt;mask-box-image&amp;gt;?&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;box-reflect：none | &amp;lt;direction&amp;gt; &amp;lt;offset&amp;gt;? &amp;lt;mask-box-image&amp;gt;?&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;取值：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; none：无倒影&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;lt;direction&amp;gt;　Demo: 简单图片倒影 See with Webkit&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; above：指定倒影在对象的上边&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; below：指定倒影在对象的下边&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; left：指定倒影在对象的左边&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; right：指定倒影在对象的右边&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;lt;offset&amp;gt;　Demo: 图片与倒影间隔 See with Webkit&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;lt;length&amp;gt;：用长度值来定义倒影与对象之间的间隔。可以为负值&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;lt;percentage&amp;gt;：用百分比来定义倒影与对象之间的间隔。可以为负值&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;lt;mask-box-image&amp;gt;　Demo: 更真实的图片倒影 文字倒影与渐变 See with Webkit&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; none：无遮罩图像&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;lt;url&amp;gt;：使用绝对或相对地址指定遮罩图像。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;lt;linear-gradient&amp;gt;：使用线性渐变创建遮罩图像。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;lt;radial-gradient&amp;gt;：使用径向(放射性)渐变创建遮罩图像。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;lt;repeating-linear-gradient&amp;gt;：使用重复的线性渐变创建背遮罩像。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;lt;repeating-radial-gradient&amp;gt;：使用重复的径向(放射性)渐变创建遮罩图像。&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;图片倒影：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;假设我们需要给一张图片做倒影效果，其结构其实非常简单：&lt;/p&gt;&lt;div class=&quot;box-reflect&quot; style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/css3/2014-12-09/1cba05d9e0c7cfcfa897394b85f7e5f9.jpg&quot; alt=&quot;使用CSS3制作图片倒影&quot; width=&quot;245&quot; height=&quot;200&quot; style=&quot;margin: 0px; padding: 0px; display: block;&quot;/&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;根据前面的语法介绍，要让倒影在对象的右侧，我们只需要给box-reflect属性取值为right即可：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;.box-reflect img {&lt;/p&gt;&lt;ol class=&quot;properties monospace&quot; style=&quot;margin-right: 0px; margin-left: 0px; padding: 2px 4px 0px 0px; font-family: Consolas, &amp;quot;Lucida Console&amp;quot;, monospace; list-style: none; clear: both; color: rgb(34, 34, 34); white-space: nowrap;&quot;&gt;&lt;li title=&quot;&quot; style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; padding: 0px 0px 0px 38px; white-space: normal; text-overflow: ellipsis; overflow: hidden; cursor: auto; margin-left: 0px !important;&quot;&gt;&lt;span class=&quot;webkit-css-property&quot; title=&quot;-webkit-box-reflect: right;&quot; style=&quot;margin: 0px 0px 0px -38px; padding: 0px; color: rgb(200, 0, 0);&quot;&gt;-webkit-box-reflect&lt;/span&gt;:&amp;nbsp;&lt;span class=&quot;value&quot; style=&quot;margin: 0px; padding: 0px;&quot;&gt;right&lt;/span&gt;;&lt;/li&gt;&lt;/ol&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;span style=&quot;font-family:Consolas, Lucida Console, monospace;margin: 0px; padding: 0px;&quot;&gt;box-reflect:right;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;}&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;效果如图：&lt;/p&gt;&lt;div style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/css3/2014-12-09/1cba05d9e0c7cfcfa897394b85f7e5f9.jpg&quot; alt=&quot;使用CSS3制作图片倒影&quot; width=&quot;245&quot; height=&quot;200&quot; style=&quot;margin: 0px; padding: 0px; display: block; -webkit-box-reflect: right 0px;&quot;/&gt;&lt;/div&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;img src=&amp;quot;图片.jpg&amp;quot; alt=&amp;quot;使用CSS3制作图片倒影&amp;quot; style=&amp;quot;-webkit-box-reflect: right;box-reflect:right&amp;quot;&amp;gt;&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;文字倒影：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;要让倒影在对象的下方，我们只需要给box-reflect属性取值为below即可：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;-webkit-box-reflect: below;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;box-reflect: below;&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px; -webkit-box-reflect: below 0px;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; color: rgb(51, 153, 102);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;记录·回忆&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;特别提示：在此示例中，我们没有为倒影提供了一个空白区，所以下面这一行就占据了位置，要解决这个问题，就得提供一个空白区域出来，使用margin：margin:20px 0;&lt;/p&gt;&lt;p style=&quot;margin-top: 20px; margin-bottom: 20px; padding: 0px; -webkit-box-reflect: below 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; color: rgb(51, 153, 102);&quot;&gt;记录·回忆&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;倒影与对象之间的间距：&lt;/strong&gt;&lt;/p&gt;&lt;div style=&quot;margin: 0px; padding: 0px;&quot;&gt;了解完生成倒影的方向之后，我们一起来看第二个属性值&amp;lt;offset&amp;gt;。有时候大家可能会有这样的需求，我虽然将倒影生成出来了，但我想让生成的倒影与对象(原图)之间有一定的间距。那么这个时候我们就需要使用到box-reflect属性中的第二个属性值&amp;lt;offset&amp;gt;。&lt;/div&gt;&lt;div style=&quot;margin: 0px; padding: 0px;&quot;&gt;随便拿一个示例来说做演示，假设对象生成的倒影在底部，而且需要让生成的倒影离自身有一个20px的间距，此时我们只需要这样做：&lt;/div&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;.box-reflect img {&lt;/p&gt;&lt;ol class=&quot;properties monospace&quot; style=&quot;margin-right: 0px; margin-left: 0px; padding: 2px 4px 0px 0px; font-family: Consolas, &amp;quot;Lucida Console&amp;quot;, monospace; list-style: none; clear: both; color: rgb(34, 34, 34); white-space: nowrap;&quot;&gt;&lt;li title=&quot;&quot; style=&quot;margin-top: 0px; margin-right: 0px; margin-bottom: 0px; padding: 0px 0px 0px 38px; white-space: normal; text-overflow: ellipsis; overflow: hidden; cursor: auto; margin-left: 0px !important;&quot;&gt;&lt;span class=&quot;webkit-css-property&quot; title=&quot;-webkit-box-reflect: right;&quot; style=&quot;margin: 0px 0px 0px -38px; padding: 0px; color: rgb(200, 0, 0);&quot;&gt;-webkit-box-reflect&lt;/span&gt;:&amp;nbsp;&lt;span class=&quot;value&quot; style=&quot;margin: 0px; padding: 0px;&quot;&gt;right 20px&lt;/span&gt;;&lt;/li&gt;&lt;/ol&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;span style=&quot;font-family:Consolas, Lucida Console, monospace;margin: 0px; padding: 0px;&quot;&gt;box-reflect:right 20px;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;}&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;效果如图：&lt;/p&gt;&lt;div style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;img src=&quot;http://www.yangqq.com/d/file/jstt/css3/2014-12-09/1cba05d9e0c7cfcfa897394b85f7e5f9.jpg&quot; alt=&quot;使用CSS3制作图片倒影&quot; width=&quot;245&quot; height=&quot;200&quot; style=&quot;margin: 0px; padding: 0px; display: block; -webkit-box-reflect: right 20px;&quot;/&gt;&lt;/div&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;lt;img src=&amp;quot;图片.jpg&amp;quot; alt=&amp;quot;使用CSS3制作图片倒影&amp;quot; style=&amp;quot;-webkit-box-reflect: right 20px;box-reflect:right 20px&amp;quot;&amp;gt;&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;给倒影添加遮罩效果:&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;左右倒影：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; -webkit-box-reflect: left 1px -webkit-gradient(linear, 0% 100%,100% 100%,from(rgba(255,255,255,0)), to(white));&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; -webkit-box-reflect: right 1px -webkit-gradient(linear, 0% 100%,100% 100%, from(rgba(255,255,255,0)), to(white));&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;上下倒影：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; -webkit-box-reflect: above 1px -webkit-gradient(linear, 0% 100%,100% 100%, from(rgba(255,255,255,0)), to(white));&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp;-webkit-box-reflect: below 1px -webkit-gradient(linear, 0% 100%,100% 100%, from(rgba(255,255,255,0)), to(white));&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;字体倒影：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; -webkit-box-reflect: below 1px -webkit-gradient(linear, 0 0, 0 100%, from(transparent), color-stop(.5, transparent), to(rgba(3,3,3,.2)));&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;文字渐变+倒影：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; -webkit-box-reflect: below 1px -webkit-gradient(linear, 0 0, 0 100%, from(transparent), color-stop(.5, transparent), to(rgba(3,3,3,.2)));&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; background: -webkit-linear-gradient(top, rgba(174,188,191,1) 0%,rgba(110,119,116,1) 50%,rgba(10,14,10,1) 51%,rgba(10,8,9,1) 100%);&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; -webkit-background-clip: text;-webkit-text-fill-color: transparent;&lt;/p&gt;&lt;/ul&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','upload/2016-06-07/575610e8d2b2f.jpg','杨青',0,' \r\n              CSS3制作文字、图片倒影需要涉及到使用CSS3.0新属性之box-reflect。box-reflect属性目前仅在Chrome、Safari和Opera浏览器下支持，但这并不影响我们来学习这个属性的应用。',7,10,1465258216,0),(9,'关于响应式Web设计技巧以及入门','&lt;ul class=&quot;infos&quot; style=&quot;margin: 20px 30px 20px 0px; padding: 0px; list-style: none; overflow: hidden; line-height: 28px; font-size: 14px; text-indent: 2em; color: rgb(102, 102, 102); font-family: 宋体, Arial, Helvetica, sans-serif; white-space: normal;&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;html5和css3流行至今，我在做响应式的网站一直是在“尝试”的阶段。并没有深入的去研究和学习，浅显的理解就是根据屏幕分辨率的大小，网站布局、图片、文字大小等相应改变。以后使用平板、手机浏览网站的用户会越来越多，对于前端设计师，学好用html5、css3做响应式网站是势在必行。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;响应式网页设计这个术语，由伊桑马克特提出，他在Alist Apart 发表了一篇开创性的文章，将三种已有的开发技巧（弹性网格布局、弹性图片、媒体和媒体查询）整合起来，并命名为响应式网页布局。还有一些其他叫法，如流式设计、弹性布局、塑料布局、流体设计、自适应布局、跨设备设计以及弹性设计。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;以往我们显示针对桌面电脑来进行宽度设计，然后将其缩小并针对小屏幕进行内容重排；现在我们应该首先针对小屏幕进行设计，然后逐步增强针对大屏幕的设计和内容。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;三个简单步骤，让你的网站变成响应式网站&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;ios和Android浏览器都基于Webkit核心。这两种浏览器以及很多其他浏览器如chrome、opera，都支持用viewport meta元素覆盖默认的画布缩放设置，只需要在html的&amp;lt;head&amp;gt;标签中插入一个&amp;lt;meta&amp;gt;标签。&amp;lt;meta&amp;gt;标签中可以设置具体的宽度或者缩放比例。示例：&lt;/p&gt;&lt;p class=&quot;shili&quot; style=&quot;margin-top: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&amp;lt;meta name=&amp;quot;viewport&amp;quot; content=&amp;quot;width=device-width,initial-scale=2,maximum-scale=3,user-scalable=no&amp;quot;&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;分析：width=device-width告诉浏览器页面的宽度应该等于设备宽度；initial-scale=2页面的缩放比例，设置比例为设备尺寸的2倍；maximum-scale=3,允许用户将页面最多放大至设备宽度的3倍；user-scalable=no禁止用户缩放。缩放是一个重要的辅助功能，所以实践中很少禁用。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;一：粘贴下面的代码到&amp;lt;head&amp;gt;和&amp;lt;/head&amp;gt;标签之间:&lt;/strong&gt;&lt;/p&gt;&lt;p class=&quot;shili&quot; style=&quot;margin-top: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&amp;lt;meta name=&amp;quot;viewport&amp;quot; content=&amp;quot;width=device-width,initial-scale=1.0&amp;quot;&amp;gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;设置比例为1.0这表示浏览器将按照其视口的实际大小来渲染页面&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;二：针对不同视口宽度修正设计&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;设置viewport meta 标签后，现在我们针对不同视口修正设计效果，创建CSS样式表，并在页面中调用：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;常见媒体查询&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;/* 平板电脑布局: 481px 至 768px。样式继承自: 移动设备布局。 */&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;@media only screen and (min-width: 481px) {&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;.class{&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;margin: 0px; padding: 0px; white-space: pre;&quot;&gt;	&lt;/span&gt;background: #333;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;}&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;}&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;/* 桌面电脑布局: 769px 至最高 1232px。样式继承自: 移动设备布局和平板电脑布局。 */&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;@media only screen and (min-width: 769px) {&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp;.class {&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; background: #666;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; }&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;}&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;三：字体、弹性图片、视频&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;对于响应式网站来说应该有响应式的字体，一个响应式的字体大小应关联它的父容器的宽度，这样它才可以适应客户端的屏幕。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;CSS3 规范引入了一个新的单位叫 rem，和 em 类相似，但相对于 HTML 元素来说， rem 更易于使用。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;rem 是相对于 HTML 元素的，不要忘了重置 HTML 的字体大小：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;html { font-size:100%; }&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;完成后，您可以定义响应式的字体大小，如下所示：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;@media (min-width: 640px) { body {font-size:1rem;} }&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;@media (min-width:960px) { body {font-size:1.2rem;} }&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;@media (min-width:1100px) { body {font-size:1.5rem;} }&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;请注意，旧浏览器不支持 rem 单元，所以不要忘了实现一个替代。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;弹性图片&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;我们需要为图片设置max-width:100%和height:auto，来实现其弹性化。对于IE，仍然需要一点额外的工作：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;img {&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; max-width: 100%;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; height: auto;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; width: auto\\9; /* ie8 */&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;}&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;弹性内嵌视频&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;同样的，对于视频，我们也需要做max-width： 100%的设置；但是Safari对embed的该属性支持不是很给力，所以我们以width： 100%来代替：&lt;/p&gt;&lt;div class=&quot;shili&quot; style=&quot;margin: 0px; padding: 10px; border: 1px dotted rgb(119, 136, 85); word-wrap: break-word; background-color: rgb(245, 245, 245);&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;.video embed,&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;.video object,&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;.video iframe {&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; width: 100%;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&amp;nbsp; &amp;nbsp; height: auto;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;}&lt;/p&gt;&lt;/div&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;参考网站：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;1、&lt;a href=&quot;http://www.yangqq.com/download/div/2014-04-17/661.html&quot; target=&quot;_blank&quot; style=&quot;margin: 0px; padding: 0px; color: rgb(51, 51, 51);&quot;&gt;黑色Html5个人博客模板主题《如影随形》&lt;/a&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;a href=&quot;http://www.yangqq.com/download/div/2014-04-17/661.html&quot; target=&quot;_blank&quot; style=&quot;margin: 0px; padding: 0px; color: rgb(51, 51, 51);&quot;&gt;&lt;img src=&quot;http://www.yangqq.com/d/file/download/div/2014-04-17/a5d40a42129d1a1e1099d62a20bc0fc1.jpg&quot; alt=&quot;黑色Html5个人博客模板主题《如影随形》&quot; style=&quot;margin: auto; padding: 0px; display: block;&quot;/&gt;&lt;/a&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;2、&lt;a href=&quot;http://www.yangqq.com/jstt/css3/2014-05-05/662.html&quot; target=&quot;_blank&quot; style=&quot;margin: 0px; padding: 0px; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;text-decoration:underline;margin: 0px; padding: 0px;&quot;&gt;《响应式纵向时间轴》&lt;/span&gt;&lt;/a&gt;&lt;/p&gt;&lt;/ul&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','upload/2016-06-07/57561153b2800.jpg','杨青',0,' \r\n              html5和css3流行至今，我在做响应式的网站一直是在“尝试”的阶段。并没有深入的去研究和学习，浅显的理解就是根据屏幕分辨率的大小，网站布局、图片、文字大小等相应改变。以后使用平板、手机浏览网站的用户会越来越多，对于前端设计师，学好用html5、css3做响应式网站是势在必行。',7,12,1465258323,0);

